mapper.select:

SELECT * FROM (
  SELECT ROWNUM rnum, n.*
  FROM (
    SELECT *
    FROM qnaboard
    WHERE ntitle LIKE '%' || #{nSearch} || '%' OR ncontent LIKE '%' || #{nSearch} || '%'
    ORDER BY nno DESC
  ) n
) 
WHERE rnum BETWEEN #{start} AND #{end}


SELECT * FROM (
  SELECT ROWNUM rnum, n.*
  FROM (
    SELECT * 
    FROM qnaboard
    ORDER BY nno DESC
  ) n
)
WHERE rnum BETWEEN #{start} AND #{end}

mapper.update:
UPDATE qnaboard
SET ntitle = #{ntitle}, ncontent = #{ncontent}
WHERE nno = #{nno}

UPDATE qnaboard
SET nview = nview + 1
WHERE nno = #{nno}

UPDATE qnareply
SET ntitle = #{ntitle}, ncontent = #{ncontent}
WHERE rno = #{rno}
